const popupContent = {
  textAlign: "center",
  height: "350px",
  width: "100%",
  marginTop: "30px",
};
const popupHead = {
  backgroundColor: "black",
  height: "35%",
  width: "45%",
  backgroundColor: "black",
  marginTop: "11%",
  borderRadius: "88%",
  position: "relative",
  zIndex: "2",
};

const avatarStyle = {
  height: "122px",
  width: "121px",
  marginLeft: "40px",
  zIndex: "1",
  right: "14%",
};

const nameContent = {
  backgroundColor: "green",
  position: "relative",
  top: "-29%",
  zIndex: "0",
  left: "38%",
  height: "11%",
  width: "54%",
};
const rankedContent = {
  position: "relative",
  top: "-26%",
  zIndex: "0",
  left: "38%",
  height: "11%",
  width: "54%",
};

const popupText = {
  fontSize: "15px",
  marginBottom: "20px",
};

const okText = {
  fontSize: "15px",
};

const avatarFormStyle = {
  backgroundImage:
    "linear-gradient(225deg, #edd65d 0, #dcd657 8.33%, #cad653 16.67%, #b6d450 25%, #a0d24e 33.33%, #87d04e 41.67%, #6acc50 50%, #45c854 58.33%, #00c45a 66.67%, #00c163 75%, #00bd6d 83.33%, #00ba78 91.67%, #00b785 100%)",
  margin: "auto",
};
const deleteIcon = {
  position: "absolute",
  left: 0,
  top: "1%",
};
const iconStyle = {
  fontSize: "2rem",
  color: "red",
};

export {
  popupContent,
  popupHead,
  popupText,
  okText,
  nameContent,
  rankedContent,
  avatarStyle,
  avatarFormStyle,
  deleteIcon,
  iconStyle,
};
